/* bxslider */
$(document).ready(function() {
  $(".slider").bxSlider({
    speed: 300,
    pagerCustom: "#navbar-list"
  });
});

/* Global Variables */
const body = document.querySelector("body"),
      serviceLink = document.querySelector("#service-link"),
      services = document.querySelector("#services");
      serviceSlide = document.querySelector("#service-slide");


/* Navigate to the top of the page */
$(document).ready(function() {
  $(".nav-link").click(function() {
    var $target = $('html, body');
    $target.animate({scrollTop: '0px'}, 100);
   
  });
});

function serviceScroll() {
  if (serviceLink.classList.contains("active")) {
    body.style.overflow = "visible";
    services.style.display = 'block'
  } else {
    body.style.overflow = "hidden";
    services.style.display = 'none'
  }
}

body.addEventListener("click", function() {
  serviceScroll();
});
body.addEventListener("touchend", function() {
  console.log(123658);
  serviceScroll();
});


